def is_prime(num):
    if num < 2:
        return False
    for i in range(2, int(num**0.5) + 1):
        if num % i == 0:
            return False
    return True

def can_be_written_as_sum_of_two_primes(n):
    for i in range(2, n):
        if is_prime(i) and is_prime(n - i):
            return True
    return False

# Test the program
n = 20
if can_be_written_as_sum_of_two_primes(n):
    print("Yes")
else:
    print("No")

